import warn from '../media/warning.png';
import ls1 from '../media/ls-icon1.png';
import ls2 from '../media/ls-icon2.png';
import ls3 from '../media/ls-icon3.png';
import ran from '../media/random.png';
import start from './start.module.css';
import * as st from "./start";
import {Component} from "react";

export default class Reg extends Component
{
    componentDidMount() {
        console.log('I was triggered during componentDidMount');
        st.ini();
    }

    componentWillUnmount() {
        console.log('I was triggered during componentWillUnmount');
    }

    render() {
        console.log('I was triggered during render');
        return(
            <div className="App">
                <header className="App-header">
                    <form action="#" className={start.posit} id="posform">
                        <header className={start.help}>
                            <span className={start.v}>Есть аккаунт? <a className={start.helpa} onClick={() => st.onvxod()}>Вход!</a></span>
                        </header>
                        <div className={start.reg} id="reg">
                            <div className={start.logo}>
                                <p>Выберите иконку для профиля:</p>
                                <input id="ch1" name="ico" type="radio" value="1" defaultChecked/><img className={start.logoi} src={ls1} alt=""/>
                                <input id="ch2" name="ico" type="radio" value="2"/><img className={start.logoi} src={ls2} alt=""/>
                                <input id="ch3" name="ico" type="radio" value="3"/><img className={start.logoi} src={ls3} alt=""/>
                            </div>
                            <div>
                                <input className={start.login} type="text" placeholder="Логин" id="logr" required pattern="^[a-zA-Z0-9]+$"/>
                            </div>
                            <div>
                                <input className={start.pass} type="password" placeholder="Пароль" id="pasr" required pattern="^[a-zA-Z0-9]+$"/>
                                <span className={start.rand} onClick={() => st.gen_pas()}><img src={ran} className={start.randimg} alt=""/>Случайный пароль</span>
                            </div>
                            <span className={start.warn} id="warncr"><img src={warn} className={start.warnimg} alt=""/>Включён Caps Lock!</span>
                            <div className={start.per}/>
                            <div className={start.lic}><input type="checkbox" name="a" value="согл"/> Принимаю условия <span className={start.url}>соглашения</span></div>
                            <div className={start.button} onClick={() => st.rego()} >ЗАРЕГИСТРИРОВАТЬСЯ!</div>
                            <div className={start.per}/>
                        </div>
                    </form>
                    <div className="warne" id="warnew"><img src={warn} className={start.warnimg} alt=""/>Внимание! <p>Неверный логин или пароль</p></div>
                    <div className="warne" id="warnev"><img src={warn} className={start.warnimg} alt=""/>Внимание! <p>Допустимы только латинница и цифры</p></div>
                    <div className="warne" id="warner"><img src={warn} className={start.warnimg} alt=""/>Внимание! <p id="wt">Допустимы только латинница и цифры</p></div>
                    <div/>
                </header>
            </div>
        );
    }
}
