import { INCREMENT, DECREMENT, RESET } from './actions';

const initialState = {
    count: 0
};

export default function reducer(state = initialState, action) {
    console.log('reducer', state, action);
    switch(action.type) {
        case INCREMENT:
            return {
                count: state.reducer1.count + 1
            };
        case DECREMENT:
            return {
                count: state.reducer1.count - 1
            };
        case RESET:
            return {
                count: 0
            };
        default:
            return state;
    }
}
