import {CHANGE_CHECKBOX} from './actions';

const initialState = {
    checkboxes: {
        "0": false // id : checked state
    }
};

export default function reducer(state = initialState, action) {
    console.log('reducer', state, action);
    switch(action.type) {
        case CHANGE_CHECKBOX:
            return {
                ...state,
                checkboxes: {
                    ...state.checkboxes,
                    [action.payload.checkBoxId]: action.payload.checkBoxState // In payload we will pass checkbox id and checkbox state
                }
            };
        default:
            return state;
    }
}
