0449\u0435\u0433\u043E \u043D\u0430 \u043E\u0441\u043D\u043E\u0432\u0430\u043D\u0438\u0438 __________________________, \u0441 \u0434\u0440\u0443\u0433\u043E\u0439 \u0441\u0442\u043E\u0440\u043E\u043D\u044B, \u0434\u0430\u043B\u0435\u0435 \u0441\u043E\u0432\u043C\u0435\u0441\u0442\u043D\u043E \u0438\u043C\u0435\u043D\u0443\u0435\u043C\u044B\u043C\u0438 \xAB\u0421\u0442\u043E\u0440\u043E\u043D\u044B\xBB. \u0421\u0442\u043E\u0440\u043E\u043D\u044B \u0434\u043E\u0433\u043E\u0432\u043E\u0440\u0438\u043B\u0438\u0441\u044C \u043E \u043D\u0438\u0436\u0435\u0441\u043B\u0435\u0434\u0443\u044E\u0449\u0435\u043C: 1.     \u041F\u0420\u0415\u0414\u041C\u0415\u0422 \u0414\u041E\u0413\u041E\u0412\u041E\u0420\u0410 \u0421\u0442\u043E\u0440\u043E\u043D\u044B \u0434\u043E\u0433\u043E\u0432\u043E\u0440\u0438\u043B\u0438\u0441\u044C \u043E \u043D\u0438\u0436\u0435\u0441\u043B\u0435\u0434\u0443\u044E\u0449\u0435\u043C: 1.1. \u041F\u0440\u0430\u0432\u043E\u043E\u0431\u043B\u0430\u0434\u0430\u0442\u0435\u043B\u044C \u043F\u0440\u0435\u0434\u043E\u0441\u0442\u0430\u0432\u043B\u044F\u0435\u0442 \u041A\u043B\u0438\u0435\u043D\u0442\u0443 \u043F\u0440\u0430\u0432\u043E \u043D\u0430 \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u043D\u0438\u0435 \u043F\u0440\u043E\u0433\u0440\u0430\u043C\u043C\u043D\u043E\u0433\u043E \u043E\u0431\u0435\u0441\u043F\u0435\u0447\u0435\u043D\u0438\u044F \xAB\u0421\u0438\u0441\u0442\u0435\u043C\u0430 \u0443\u043F\u0440\u0430\u0432\u043B\u0435\u043D\u0438\u044F \u0441\u0430\u0439\u0442\u0430\u043C\u0438 UMI.CMS\xBB \u043D\u0430 \u0443\u0441\u043B\u043E\u0432\u0438\u044F\u0445 \u043F\u0440\u043E\u0441\u0442\u043E\u0439 (\u043D\u0435\u0438\u0441\u043A\u043B\u044E\u0447\u0438\u0442\u0435\u043B\u044C\u043D\u043E\u0439) \u043B\u0438\u0446\u0435\u043D\u0437\u0438\u0438 (\u0434\u0430\u043B\u0435\u0435 \u2013 \xAB\u0421\u0438\u0441\u0442\u0435\u043C\u0430\xBB) \u0441\u043F\u043E\u0441\u043E\u0431\u0430\u043C\u0438, \u0443\u043A\u0430\u0437\u0430\u043D\u043D\u044B\u043C\u0438 \u043D\u0438\u0436\u0435. \u0421\u043E\u0441\u0442\u0430\u0432 \u0438 \u043D\u0430\u0438\u043C\u0435\u043D\u043E\u0432\u0430\u043D\u0438\u044F \u0440\u0435\u0434\u0430\u043A\u0446\u0438\u0439 \u0421\u0438\u0441\u0442\u0435\u043C, \u043F\u0440\u0430\u0432\u0430 \u043D\u0430 \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u043D\u0438\u0435 \u043A\u043E\u0442\u043E\u0440\u044B\u0445 \u043F\u0440\u0435\u0434\u043E\u0441\u0442\u0430\u0432\u043B\u044F\u044E\u0442\u0441\u044F (\u043F\u0435\u0440\u0435\u0434\u0430\u044E\u0442\u0441\u044F) \u041A\u043B\u0438\u0435\u043D\u0442\u0443, \u0443\u043A\u0430\u0437\u044B\u0432\u0430\u0435\u0442\u0441\u044F \u0432 \u0441\u0447\u0451\u0442\u0435, \u0432\u044B\u0441\u0442\u0430\u0432\u043B\u044F\u0435\u043C\u043E\u043C \u041F\u0440\u0430\u0432\u043E\u043E\u0431\u043B\u0430\u0434\u0430\u0442\u0435\u043B\u0435\u043C \u043D\u0430 \u043E\u0441\u043D\u043E\u0432\u0430\u043D\u0438\u0438 \u043F\u0440\u0435\u0434\u0432\u0430\u0440\u0438\u0442\u0435\u043B\u044C\u043D\u043E\u0439 \u0437\u0430\u044F\u0432\u043A\u0438 \u041A\u043B\u0438\u0435\u043D\u0442\u0430, \u0430 \u0442\u0430\u043A\u0436\u0435 \u0432 \u0410\u043A\u0442\u0430\u0445 \u043F\u0435\u0440\u0435\u0434\u0430\u0447\u0438 \u043F\u0440\u0430\u0432. 1.2. \u0417\u0430 \u043F\u0440\u0435\u0434\u043E\u0441\u0442\u0430\u0432\u043B\u044F\u0435\u043C\u044B\u0435 \u043F\u043E \u043D\u0430\u0441\u0442\u043E\u044F\u0449\u0435\u043C\u0443 \u0414\u043E\u0433\u043E\u0432\u043E\u0440\u0443 \u043F\u0440\u0430\u0432\u0430 \u041A\u043B\u0438\u0435\u043D\u0442 \u043E\u0431\u044F\u0437\u0443\u0435\u0442\u0441\u044F \u0443\u043F\u043B\u0430\u0447\u0438\u0432\u0430\u0442\u044C \u041F\u0440\u0430\u0432\u043E\u043E\u0431\u043B\u0430\u0434\u0430\u0442\u0435\u043B\u044E \u0432\u043E\u0437\u043D\u0430\u0433\u0440\u0430\u0436\u0434\u0435\u043D\u0438\u0435, \u0440\u0430\u0437\u043C\u0435\u0440 \u043A\u043E\u0442\u043E\u0440\u043E\u0433\u043E \u043E\u043F\u0440\u0435\u0434\u0435\u043B\u