\u043A\u043E\u043F\u0438\u0440\u043E\u0432\u0430\u043D\u0438\u044F \u0438 \u0437\u0430\u043F\u0443\u0441\u043A\u0430 \u0432 \u0441\u043E\u043E\u0442\u0432\u0435\u0442\u0441\u0442\u0432\u0438\u0438 \u0441 \u0441\u043E\u043F\u0440\u043E\u0432\u043E\u0434\u0438\u0442\u0435\u043B\u044C\u043D\u043E\u0439 \u0434\u043E\u043A\u0443\u043C\u0435\u043D\u0442\u0430\u0446\u0438\u0435\u0439; * \u043F\u0440\u0430\u0432\u043E \u043D\u0430 \u043F\u0435\u0440\u0435\u0440\u0430\u0431\u043E\u0442\u043A\u0443 \u2013 \u0432 \u0442\u043E\u0439 \u0441\u0442\u0435\u043F\u0435\u043D\u0438, \u0432 \u043A\u043E\u0442\u043E\u0440\u043E\u0439 \u044D\u0442\u0430 \u0432\u043E\u0437\u043C\u043E\u0436\u043D\u043E\u0441\u0442\u044C \u043E\u043F\u0438\u0441\u0430\u043D\u0430 \u0432  \u0434\u043E\u043A\u0443\u043C\u0435\u043D\u0442\u0430\u0446\u0438\u0438 \u043A \u0421\u0438\u0441\u0442\u0435\u043C\u0435; * \u043F\u0440\u0430\u0432\u043E \u043D\u0430 \u0434\u043E\u0432\u0435\u0434\u0435\u043D\u0438\u0435 \u0434\u043E \u0432\u0441\u0435\u043E\u0431\u0449\u0435\u0433\u043E \u0441\u0432\u0435\u0434\u0435\u043D\u0438\u044F \u2013 \u0432 \u0442\u043E\u0439 \u0441\u0442\u0435\u043F\u0435\u043D\u0438, \u0432 \u043A\u043E\u0442\u043E\u0440\u043E\u0439 \u044D\u0442\u043E \u043D\u0435\u043E\u0431\u0445\u043E\u0434\u0438\u043C\u043E \u0434\u043B\u044F \u0444\u0443\u043D\u043A\u0446\u0438\u043E\u043D\u0438\u0440\u043E\u0432\u0430\u043D\u0438\u044F \u0421\u0430\u0439\u0442\u0430, \u0441\u043E\u0437\u0434\u0430\u043D\u043D\u043E\u0433\u043E \u043D\u0430 \u043E\u0441\u043D\u043E\u0432\u0430\u043D\u0438\u0438  \u0421\u0438\u0441\u0442\u0435\u043C\u044B. 2. \u041E\u0421\u041D\u041E\u0412\u041D\u042B\u0415 \u041F\u041E\u041D\u042F\u0422\u0418\u042F, \u0418\u0421\u041F\u041E\u041B\u042C\u0417\u0423\u0415\u041C\u042B\u0415 \u0412 \u0414\u041E\u0413\u041E\u0412\u041E\u0420\u0415 2.1. \xAB\u0421\u0438\u0441\u0442\u0435\u043C\u0430 \u0443\u043F\u0440\u0430\u0432\u043B\u0435\u043D\u0438\u044F \u0441\u0430\u0439\u0442\u0430\u043C\u0438 UMI.CMS\xBB - \u043F\u0440\u043E\u0433\u0440\u0430\u043C\u043C\u043D\u043E\u0435 \u043E\u0431\u0435\u0441\u043F\u0435\u0447\u0435\u043D\u0438\u0435 \u0434\u043B\u044F \u0443\u043F\u0440\u0430\u0432\u043B\u0435\u043D\u0438\u044F \u0441\u0430\u0439\u0442\u0430\u043C\u0438 \u0432 \u0441\u0435\u0442\u0438 \u0418\u043D\u0442\u0435\u0440\u043D\u0435\u0442. \u041E\u0431\u0435\u0441\u043F\u0435\u0447\u0438\u0432\u0430\u0435\u0442 \u0441\u043E\u0437\u0434\u0430\u043D\u0438\u0435 \u0441\u0430\u0439\u0442\u0430, \u0443\u043F\u0440\u0430\u0432\u043B\u0435\u043D\u0438\u0435 \u0438\u043D\u0444\u043E\u0440\u043C\u0430\u0446\u0438\u0435\u0439, \u0434\u0438\u0437\u0430\u0439\u043D\u043E\u043C, \u0440\u0435\u0433\u0438\u0441\u0442\u0440\u0430\u0446\u0438\u044E \u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u0442\u0435\u043B\u0435\u0439. \u0410\u0434\u043C\u0438\u043D\u0438\u0441\u0442\u0440\u0430\u0442\u043E\u0440\u044B \u0441\u0430\u0439\u0442\u0430 \u043F\u0440\u0438 \u043F\u043E\u043C\u043E\u0449\u0438 \u0432\u0435\u0431-\u0438\u043D\u0442\u0435\u0440\u0444\u0435\u0439\u0441\u0430 \u043C\u043E\u0433\u0443\u0442 \u0438\u0437\u043C\u0435\u043D\u044F\u0442\u044C \u0441\u043E\u0434\u0435\u0440\u0436\u0438\u043C\u043E\u0435 \u0441\u0430\u0439\u0442\u0430, \u0435\u0433\u043E \u043D\u0430\u0441\u0442\u0440\u043E\u0439\u043A\u0438 \u0438 \u043E\u0444\u043E\u0440\u043C\u043B\u0435\u043D\u0438\u0435, \u043F\u043E\u043B\u0443\u0447\u0430\u0442\u044C \u043E\u0442\u0447\u0451\u0442\u044B \u043E \u0440\u0430\u0431\u043E\u0442\u0435 \u0441\u0430\u0439\u0442\u0430 \u0438 \u0441\u043E\u043E\u0431\u0449\u0435\u043D\u0438\u044F, \u043F\u0435\u0440\u0435\u0434\u0430\u043D\u043D\u044B\u0435 \u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u0442\u0435\u043B\u044F\u043C\u0438. \u0424\u0443\u043D\u043A\u0446\u0438\u043E\u043D\u0430\u043B\u044C\u043D\u043E\u0441\u0442\u044C UMI.CMS \u043C\u043E\u0436\u0435\u0442 \u0431\u044B\u0442\u044C \u0440\u0430\u0441\u0448\u0438\u0440\u0435\u043D\u0430 \u043F\u0443\u0442\u0451\u043C \u0443\u0441\u0442\u0430\u043D\u043E\u0432\u043A\u0438 \u0434\u043E\u043F\u043E\u043B\u043D\u0438\u0442\u0435\u043B\u044C\u043D\u044B\u0445 \u043C\u043E\u0434\u0443\u043B\u0435\u0439. 2.2. \u041A\u043B\u0438\u0435\u043D\u0442 \u2014 \u0444\u0438\u0437\u0438\u0447\u0435\u0441\u043A\u043E\u0435 \u0438\u043B\u0438 \u044E\u0440\u0438\u0434\u0438\u0447\u0435\u0441\u043A\u043E\u0435 \u043B\u0438\u0446\u043E, \u043F\u0440\u0438\u043E\u0431\u0440\u0435\u0442\u0430\u044E\u0449\u0435\u0435 \u043F\u0440\u0430\u0432\u043E \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u043D\u0438\u044F \u0421\u0438\u0441\u0442\u0435\u043C\u044B \u0432 \u043F\u0440\u0435\u0434\u0435\u043B\u0430\u0445, \u0443\u0441\u0442\u0430\u043D\u043E\u0432\u043B\u0435\u043D\u043D\u044B\u0445 \u043D\u0430\u0441\u0442\u043E\u044F\u0449\u0438\u043C \u0434\u043E\u0433\u043E\u0432\u043E\u0440\u043E\u043C \u0438 \u0440\u0435\u0434\u0430\u043A\u0446\u0438\u0435\u0439 \u0421\u0438\u0441\u0442\u0435\u043C\u044B, \u0443\u0441\u043B\u043E\u0432\u0438\u044F \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u043D\u0438\u044F \u043A\u043E\u0442\u043E\u0440\u044B\u0445 \u0440\u0430\u0437\u043C\u0435\u0449\u0435\u043D\u044B \u043D\u0430 \u0441\u0442\u0440\u0430\u043D\u0438\u0446\u0435 \u0432 \u0441\u0435\u0442\u0438 \u0418\u043D\u0442\u0435\u0440\u043D\u0435\u0442 http://www.umi-cms.ru/editions/, \u0434\u043B\u044F \u0441\u043E\u0437\u0434\u0430\u043D\u0438\u044F \u0441\u043E\u0431\u0441\u0442\u0432\u0435\u043D\u043D\u043E\u0433\u043E \u0441\u0430\u0439\u0442\u0430. 2.3. \u041B\u0438\u0446\u0435\u043D\u0437\u0438\u044F (\u043B\u0438\u0446\u0435\u043D\u0437\u0438\u043E\u043D\u043D\u044B\u0439 \u0434\u043E\u0433\u043E\u0432\u043E\u0440) \u2014 \u0441\u043E\u0433\u043B\u0430\u0448\u0435\u043D\u0438\u0435 \u043C\u0435\u0436\u0434\u0443 \u041F\u0440\u0430\u0432\u043E\u043E\u0431\u043B\u0430\u0434\u0430\u0442\u0435\u043B\u0435\u043C \u0438 \u041A\u043B\u0438\u0435\u043D\u0442\u043E\u043C \u043E\u0431 \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u043D\u0438\u0438 \u0421\u0438\u0441\u0442\u0435\u043C\u044B \u041A\u043B\u0438\u0435\u043D\u0442\u043E\u043C \u0432 \u043F\u0440\u0435\u0434\u0435\u043B\u0430\u0445, \u0443\u0441\u0442\u0430\u043D\u043E\u0432\u043B\u0435\u043D\u043D\u044B\u0445 \u0434\u043E\u0433\u043E\u0432\u043E\u0440\u043E\u043C. \u041F\u043E \u043D\u0430\u0441\u0442\u043E\u044F\u0449\u0435\u043C\u0443 \u0434\u043E\u0433\u043E\u0432\u043E\u0440\u0443 \u043F\u0440\u0430\u0432\u043E \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u043D\u0438\u044F \u0421\u0438\u0441\u0442\u0435\u043C\u044B \u043F\u0440\u0435\u0434\u043E\u0441\u0442\u0430\u0432\u043B\u044F\u0435\u0442\u0441\u044F \u041A\u043B\u0438\u0435\u043D\u0442\u0443 \u043D\u0430 \u0443\u0441\u043B\u043E\u0432\u0438\u044F\u0445 \u043F\u0440\u043E\u0441\u0442\u043E\u0439 (\u043D\u0435\u0438\u0441\u043A\u043B\u044E\u0447\u0438\u0442\u0435\u043B\u044C\u043D\u043E\u0439) \u043B\u0438\u0446\u0435\u043D\u0437\u0438\u0438 \u0441 \u0441\u043E\u0445\u0440\u0430\u043D\u0435\u043D\u0438\u0435\u043C \u0437\u0430 \u041F\u0440\u0430\u0432\u043E\u043E\u0431\u043B\u0430\u0434\u0430\u0442\u0435\u043B\u0435\u043C \u043F\u0440\u0430\u0432\u0430 \u0432\u044B\u0434\u0430\u0447\u0438 \u043B\u0438\u0446\u0435\u043D\u0437\u0438\u0439 \u0434\u0440\u0443\u0433\u0438\u043C \u043B\u0438\u0446\u0430\u043C. 2.4. \u041B\u0438\u0446\u0435\u043D\u0437\u0438\u043E\u043D\u043D\u044B\u0439 \u043A\u043B\u044E\u0447 \u2013 \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u0443\u0435\u0442\u0441\u044F \u041F\u0440\u0430\u0432\u043E\u043E\u0431\u043B\u0430\u0434\u0430\u0442\u0435\u043B\u0435\u043C \u0434\u043B\u044F \u0438\u043D\u0438\u0446\u0438\u0430\u043B\u0438\u0437\u0430\u0446\u0438\u0438, \u0437\u0430\u0449\u0438\u0442\u044B \u0438 \u043F\u0440\u0435\u0434\u043E\u0442\u0432\u0440\u0430\u0449\u0435\u043D\u0438\u044F \u0421\u0438\u0441\u0442\u0435\u043C\u044B \u043E\u0442 \u043D\u0435\u0437\u0430\u043A\u043E\u043D\u043D\u043E\u0433\u043E \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u043D\u0438\u044F, \u0441\u043E\u0434\u0435\u0440\u0436\u0438\u0442 \u0438\u043D\u0444\u043E\u0440\u043C\u0430\u0446\u0438\u044E \u043E \u0440\u0435\u0434\u0430\u043A\u0446\u0438\u0438 \u0421\u0438\u0441\u0442\u0435\u043C\u044B, \u043E \u0440\u0430\u0441\u0448\u0438\u0440\u0435\u043D\u0438\u044F\u0445 \u0421\u0438\u0441\u0442\u0435\u043C\u044B, \u043E \u043C\u043E\u0434\u0443\u043B\u044F\u0445 \u0421\u0438\u0441\u0442\u0435\u043C\u044B, \u043E \u0448\u0430\u0431\u043B\u043E\u043D\u0430\u0445 \u0434\u0438\u0437\u0430\u0439\u043D\u043E\u0432, \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u0443\u0435\u043C\u044B\u0445 \u0434\u043E\u043C\u0435\u043D\u043D\u044B\u0445 \u0438\u043C\u0435\u043D\u0430\u0445 \u0438 \u044D\u043B\u0435\u043A\u0442\u0440\u043E\u043D\u043D\u043E\u0439 \u043F\u043E\u0447\u0442\u0435 \u041A\u043B\u0438\u0435\u043D\u0442\u0430, \u043F\u0440\u0430\u0432\u043E\u043C\u0435\u0440\u043D\u043E \u0432\u043B\u0430\u0434\u0435\u044E\u0449\u0435\u0433\u043E \u043B\u0438\u0446\u0435\u043D\u0437\u0438\u0435\u0439 \u043D\u0430 \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u043D\u0438\u0435 \u0421\u0438\u0441\u0442\u0435\u043C\u044B. 2.5. \u0421\u0430\u0439\u0442 \u0432 \u0441\u0435\u0442\u0438 \u0418\u043D\u0442\u0435\u0440\u043D\u0435\u0442 - \u0441\u043E\u0432\u043E\u043A\u0443\u043F\u043D\u043E\u0441\u0442\u044C \u044D\u043A\u0437\u0435\u043C\u043F\u043B\u044F\u0440\u0430 \u0441\u0438\u0441\u0442\u0435\u043C\u044B \u0438 \u0438\u043D\u043E\u0439 \u0438\u043D\u0444\u043E\u0440\u043C\u0430\u0446\u0438\u0438, \u0441\u043E\u0434\u0435\u0440\u0436\u0430\u0449\u0435\u0439\u0441\u044F \u0432 \u0431\u0430\u0437\u0435 \u0434\u0430\u043D\u043D\u044B\u0445 \u0441\u0430\u0439\u0442\u0430, \u0441\u043E\u0441\u0442\u0430\u0432\u043B\u044F\u044E\u0449\u0430\u044F \u0438\u043D\u0444\u043E\u0440\u043C\u0430\u0446\u0438\u043E\u043D\u043D\u0443\u044E \u0441\u0438\u0441\u0442\u0435\u043C\u0443, \u0434\u043E\u0441\u0442\u0443\u043F \u043A \u043A\u043E\u0442\u043E\u0440\u043E\u0439 \u043E\u0431\u0435\u0441\u043F\u0435\u0447\u0438\u0432\u0430\u0435\u0442\u0441\u044F \u043F\u043E\u0441\u0440\u0435\u0434\u0441\u0442\u0432\u043E\u043C \u0441\u0435\u0442\u0438 \u0418\u043D\u0442\u0435\u0440\u043D\u0435\u0442 \u043F\u043E \u0434\u043E\u043C\u0435\u043D\u043D\u044B\u043C \u0438\u043C\u0435\u043D\u0430\u043C \u0438 (\u0438\u043B\u0438) \u043F\u043E \u0441\u0435\u0442\u0435\u0432\u044B\u043C \u0430\u0434\u0440\u0435\u0441\u0430\u043C, \u043F\u043E\u0437\u0432\u043E\u043B\u044F\u044E\u0449\u0438\u043C \u0438\u0434\u0435\u043D\u0442\u0438\u0444\u0438\u0446\u0438\u0440\u043E\u0432\u0430\u0442\u044C \u0441\u0430\u0439\u0442\u044B \u0432 \u0441\u0435\u0442\u0438 \u0418\u043D\u0442\u0435\u0440\u043D\u0435\u0442. 2.6. IP-\u0430\u0434\u0440\u0435\u0441 (\u0441\u0435\u0442\u0435\u0432\u043E\u0439 \u0430\u0434\u0440\u0435\u0441) \u2014 \u0443\u043D\u0438\u043A\u0430\u043B\u044C\u043D\u044B\u0439 \u0438\u0434\u0435\u043D\u0442\u0438\u0444\u0438\u043A\u0430\u0442\u043E\u0440 \u043E\u0431\u043E\u0440\u0443\u0434\u043E\u0432\u0430\u043D\u0438\u044F \u0432 \u0441\u0435\u0442\u0438 \u043F\u0435\u0440\u0435\u0434\u0430\u0447\u0438 \u0434\u0430\u043D\u043D\u044B\u0445, \u043F\u043E\u0434\u043A\u043B\u044E\u0447\u0451\u043D\u043D\u043E\u0433\u043E \u043A \u0441\u0435\u0442\u0438 \u0418\u043D\u0442\u0435\u0440\u043D\u0435\u0442, \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u0443\u0435\u043C\u044B\u0439 \u0432 \u0442\u043E\u043C \u0447\u0438\u0441\u043B\u0435 \u0434\u043B\u044F \u0447\u0438\u0441\u043B\u043E\u0432\u043E\u0439 \u0438\u0434\u0435\u043D\u0442\u0438\u0444\u0438\u043A\u0430\u0446\u0438\u0438 \u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u0442\u0435\u043B\u0435\u0439 \u0438 \u0440\u0435\u0441\u0443\u0440\u0441\u043E\u0432 \u0432 \u0441\u0435\u0442\u0438. 2.7. \u0414\u043E\u043C\u0435\u043D\u043D\u043E\u0435 \u0438\u043C\u044F \u2014 \u0441\u0438\u043C\u0432\u043E\u043B\u044C\u043D\u044B\u0439 \u0438\u0434\u0435\u043D\u0442\u0438\u0444\u0438\u043A\u0430\u0442\u043E\u0440, \u043F\u0440\u0435\u0434\u043D\u0430\u0437\u043D\u0430\u0447\u0435\u043D\u043D\u044B\u0439 \u0434\u043B\u044F \u0430\u0434\u0440\u0435\u0441\u0430\u0446\u0438\u0438 \u0441\u0430\u0439\u0442\u043E\u0432 \u0432 \u0441\u0435\u0442\u0438 \u0418\u043D\u0442\u0435\u0440\u043D\u0435\u0442 \u0432 \u0446\u0435\u043B\u044F\u0445 \u043E\u0431\u0435\u0441\u043F\u0435\u0447\u0435\u043D\u0438\u044F \u0434\u043E\u0441\u0442\u0443\u043F\u0430 \u043A \u0438\u043D\u0444\u043E\u0440\u043C\u0430\u0446\u0438\u0438, \u0440\u0430\u0437\u043C\u0435\u0449\u0451\u043D\u043D\u043E\u0439 \u0432 \u0441\u0435\u0442\u0438 \u0418\u043D\u0442\u0435\u0440\u043D\u0435\u0442. 2.8. \u0412\u043B\u0430\u0434\u0435\u043B\u0435\u0446 \u0441\u0430\u0439\u0442\u0430 \u0432 \u0441\u0435\u0442\u0438 \u0418\u043D\u0442\u0435\u0440\u043D\u0435\u0442 - \u043B\u0438\u0446\u043E, \u0441\u0430\u043C\u043E\u0441\u0442\u043E\u044F\u0442\u0435\u043B\u044C\u043D\u043E \u0438 \u043F\u043E \u0441\u0432\u043E\u0435\u043C\u0443 \u0443\u0441\u043C\u043E\u0442\u0440\u0435\u043D\u0438\u044E \u043E\u043F\u0440\u0435\u0434\u0435\u043B\u044F\u044E\u0449\u0435\u0435 \u043F\u043E\u0440\u044F\u0434\u043E\u043A \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u043D\u0438\u044F \u0441\u0430\u0439\u0442\u0430 \u0432 \u0441\u0435\u0442\u0438 \u0418\u043D\u0442\u0435\u0440\u043D\u0435\u0442, \u0432 \u0442\u043E\u043C \u0447\u0438\u0441\u043B\u0435 \u043F\u043E\u0440\u044F\u0434\u043E\u043A \u0440\u0430\u0437\u043C\u0435\u0449\u0435\u043D\u0438\u044F \u0438\u043D\u0444\u043E\u0440\u043C\u0430\u0446\u0438\u0438 \u043D\u0430 \u0442\u0430\u043A\u043E\u043C \u0441\u0430\u0439\u0442\u0435. 2.9. \u0425\u043E\u0441\u0442\u0438\u043D\u0433\u043E\u0432\u0430\u044F \u043F\u043B\u043E\u0449\u0430\u0434\u043A\u0430 \u2014 \u0430\u043F\u043F\u0430\u0440\u0430\u0442\u043D\u043E \u043F\u0440\u043E\u0433\u0440\u0430\u043C\u043C\u043D\u044B\u0439 \u043A\u043E\u043C\u043F\u043B\u0435\u043A\u0441 (\u0441\u0435\u0440\u0432\u0435\u0440 \u0438\u043B\u0438 \u0433\u0440\u0443\u043F\u043F\u0430 \u0441\u0435\u0440\u0432\u0435\u0440\u043E\u0432) \u0434\u043B\u044F \u0440\u0430\u0437\u043C\u0435\u0449\u0435\u043D\u0438\u044F \u0438 \u043F\u043E\u0434\u0434\u0435\u0440\u0436\u0430\u043D\u0438\u044F \u0441\u0430\u0439\u0442\u0430 \u0432 \u0441\u0435\u0442\u0438 \u0418\u043D\u0442\u0435\u0440\u043D\u0435\u0442, \u043F\u0440\u0438\u043D\u0430\u0434\u043B\u0435\u0436\u0430\u0449\u0438\u0439 \u041A\u043B\u0438\u0435\u043D\u0442\u0443 \u0438\u043B\u0438 \u043F\u0440\u0435\u0434\u043E\u0441\u0442\u0430\u0432\u043B\u044F\u0435\u043C\u044B\u0439 \u0442\u0440\u0435\u0442\u044C\u0435\u0439 \u0441\u0442\u043E\u0440\u043E\u043D\u043E\u0439 (\u043F\u0440\u043E\u0432\u0430\u0439\u0434\u0435\u0440\u043E\u043C \u0445\u043E\u0441\u0442\u0438\u043D\u0433\u0430). 2.10. \u0420\u0430\u0441\u0448\u0438\u0440\u0435\u043D\u043D\u0430\u044F \u043A\u043E\u043C\u043C\u0435\u0440\u0447\u0435\u0441\u043A\u0430\u044F \u043B\u0438\u0446\u0435\u043D\u0437\u0438\u044F (\u0434\u0430\u043B\u0435\u0435 \u2013 \u0420\u041A\u041B) \u2014 \u043F\u0440\u0430\u0432\u043E \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u043D\u0438\u044F \u043E\u0431\u043D\u043E\u0432\u043B\u0435\u043D\u0438\u0439 (\u0432\u0435\u0440\u0441\u0438\u0439, \u043D\u043E\u0432\u044B\u0445 \u0440\u0435\u043B\u0438\u0437\u043E\u0432) \u0421\u0438\u0441\u0442\u0435\u043C\u044B, \u043D\u0430\u043F\u0440\u0430\u0432\u043B\u0435\u043D\u043D\u044B\u0445 \u043D\u0430 \u0443\u043B\u0443\u0447\u0448\u0435\u043D\u0438\u0435 \u0421\u0438\u0441\u0442\u0435\u043C\u044B. \u0414\u0430\u043D\u043D\u044B\u0439 \u0442\u0438\u043F \u043B\u0438\u0446\u0435\u043D\u0437\u0438\u0438 \u0438\u043C\u0435\u0435\u0442 \u0441\u0440\u043E\u0447\u043D\u044B\u0439 \u0445\u0430\u0440\u0430\u043A\u0442\u0435\u0440 \u0438 \u043F\u0440\u0435\u0434\u043E\u0441\u0442\u0430\u0432\u043B\u044F\u0435\u0442 \u041A\u043B\u0438\u0435\u043D\u0442\u0443 \u0432\u043E\u0437\u043C\u043E\u0436\u043D\u043E\u0441\u0442\u044C \u043F\u043E\u043B\u0443\u0447\u0430\u0442\u044C \u043E\u0431\u043D\u043E\u0432\u043B\u0435\u043D\u0438\u044F \u0421\u0438\u0441\u0442\u0435\u043C\u044B,  \u0432\u044B\u043F\u0443\u0449\u0435\u043D\u043D\u044B\u0435 \u041F\u0440\u0430\u0432\u043E\u043E\u0431\u043B\u0430\u0434\u0430\u0442\u0435\u043B\u0435\u043C \u0432 \u043F\u0435\u0440\u0438\u043E\u0434 \u0434\u0435\u0439\u0441\u0442\u0432\u0438\u044F \u0420\u041A\u041B. 2.11. \u041E\u0441\u043D\u043E\u0432\u043D\u043E\u0439 \u0441\u0430\u0439\u0442 \u041F\u0440\u0430\u0432\u043E\u043E\u0431\u043B\u0430\u0434\u0430\u0442\u0435\u043B\u044F \u2014 http://www.umi-cms.ru. 3. \u041F\u0420\u0410\u0412\u0410 \u0418 \u041E\u0411\u042F\u0417\u0410\u041D\u041D\u041E\u0421\u0422\u0418 \u0421\u0422\u041E\u0420\u041E\u041D 3.1. \u041F\u0440\u0430\u0432\u0430 \u0438 \u043E\u0431\u044F\u0437\u0430\u043D\u043D\u043E\u0441\u0442\u0438 \u041F\u0440\u0430\u0432\u043E\u043E\u0431\u043B\u0430\u0434\u0430\u0442\u0435\u043B\u044F. 3.1.1. \u041F\u0440\u0430\u0432\u043E\u043E\u0431\u043B\u0430\u0434\u0430\u0442\u0435\u043B\u044C \u043F\u0440\u0435\u0434\u043E\u0441\u0442\u0430\u0432\u043B\u044F\u0435\u0442 \u041A\u043B\u0438\u0435\u043D\u0442\u0443 \u043F\u0440\u0430\u0432\u043E \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u043D\u0438\u044F \u044D\u043A\u0437\u0435\u043C\u043F\u043B\u044F\u0440\u0430 \u0421\u0438\u0441\u0442\u0435\u043C\u044B \u0438 \u043E\u0431\u043D\u043E\u0432\u043B\u0435\u043D\u0438\u0439  \u043A \u043D\u0435\u0439 \u0432 \u0442\u0435\u0447\u0435\u043D\u0438\u0435 \u043E\u0434\u043D\u043E\u0433\u043E \u043F\u0435\u0440\u0432\u043E\u0433\u043E \u0433\u043E\u0434\u0430 \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u043D\u0438\u044F \u0421\u0438\u0441\u0442\u0435\u043C\u044B \u043D\u0430 \u0443\u0441\u043B\u043E\u0432\u0438\u044F\u0445 100% \u043F\u0440\u0435\u0434\u043E\u043F\u043B\u0430\u0442\u044B \u0443\u043A\u0430\u0437\u0430\u043D\u043D\u043E\u0433\u043E \u0432 \u0441\u0447\u0435\u0442\u0435 \u0432\u043E\u0437\u043D\u0430\u0433\u0440\u0430\u0436\u0434\u0435\u043D\u0438\u044F. \u041F\u0440\u0430\u0432\u043E \u043D\u0430 \u043F\u043E\u043B\u0443\u0447\u0435\u043D\u0438\u0435 \u043E\u0431\u043D\u043E\u0432\u043B\u0435\u043D\u0438\u0439 \u043D\u0430\u0447\u0438\u043D\u0430\u044F \u0441\u043E \u0432\u0442\u043E\u0440\u043E\u0433\u043E \u0433\u043E\u0434\u0430 \u043E\u0431\u0443\u0441\u043B\u043E\u0432\u043B\u0435\u043D\u043E \u043F\u0440\u0438\u043E\u0431\u0440\u0435\u0442\u0435\u043D\u0438\u0435\u043C  \u0420\u041A\u041B. 3.1.2. \u041F\u0440\u0430\u0432\u043E\u043E\u0431\u043B\u0430\u0434\u0430\u0442\u0435\u043B\u044C \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u0443\u0435\u0442 \u043B\u0438\u0446\u0435\u043D\u0437\u0438\u043E\u043D\u043D\u044B\u0439 \u043A\u043B\u044E\u0447 \u0434\u043B\u044F \u0438\u043D\u0438\u0446\u0438\u0430\u043B\u0438\u0437\u0430\u0446\u0438\u0438, \u0437\u0430\u0449\u0438\u0442\u044B \u0438 \u043F\u0440\u0435\u0434\u043E\u0442\u0432\u0440\u0430\u0449\u0435\u043D\u0438\u044F \u0421\u0438\u0441\u0442\u0435\u043C\u044B \u043E\u0442 \u043D\u0435\u0437\u0430\u043A\u043E\u043D\u043D\u043E\u0433\u043E \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u043D\u0438\u044F. 3.1.3. \u041F\u043E \u043E\u0434\u043D\u043E\u0439 \u043B\u0438\u0446\u0435\u043D\u0437\u0438\u0438 \u041A\u043B\u0438\u0435\u043D\u0442\u0443 \u043F\u0440\u0435\u0434\u043E\u0441\u0442\u0430\u0432\u043B\u044F\u0435\u0442\u0441\u044F \u043F\u0440\u0430\u0432\u043E \u0441\u043E\u0437\u0434\u0430\u0442\u044C \u043E\u0434\u0438\u043D \u0441\u043E\u0431\u0441\u0442\u0432\u0435\u043D\u043D\u044B\u0439 \u0421\u0430\u0439\u0442 \u043D\u0430 \u0431\u0430\u0437\u0435 \u043E\u0434\u043D\u043E\u0433\u043E \u044D\u043A\u0437\u0435\u043C\u043F\u043B\u044F\u0440\u0430 \u0421\u0438\u0441\u0442\u0435\u043C\u044B. \u0421\u043E\u0437\u0434\u0430\u043D\u0438\u0435 \u0431\u043E\u043B\u044C\u0448\u0435\u0433\u043E \u0447\u0438\u0441\u043B\u0430 \u0421\u0430\u0439\u0442\u043E\u0432 \u0432\u043E\u0437\u043C\u043E\u0436\u043D\u043E \u0442\u043E\u043B\u044C\u043A\u043E \u043F\u0440\u0438 \u0443\u0441\u043B\u043E\u0432\u0438\u0438 \u043F\u0440\u0438\u043E\u0431\u0440\u0435\u0442\u0435\u043D\u0438\u044F \u0434\u043E\u043F\u043E\u043B\u043D\u0438\u0442\u0435\u043B\u044C\u043D\u044B\u0445 \u044D\u043A\u0437\u0435\u043C\u043F\u043B\u044F\u0440\u043E\u0432 \u0421\u0438\u0441\u0442\u0435\u043C\u044B. \u0418\u0441\u043A\u043B\u044E\u0447\u0435\u043D\u0438\u0435 \u0438\u0437 \u044D\u0442\u043E\u0433\u043E \u0443\u0441\u043B\u043E\u0432\u0438\u044F \u0441\u043E\u0441\u0442\u0430\u0432\u043B\u044F\u0435\u0442 \u0442\u043E\u043B\u044C\u043A\u043E \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u043D\u0438\u0435 \u0441\u043F\u0435\u0446\u0438\u0430\u043B\u044C\u043D\u043E\u0439 \u0444\u0443\u043D\u043A\u0446\u0438\u0438 \u0421\u0438\u0441\u0442\u0435\u043C\u044B (\u043D\u0430\u043B\u0438\u0447\u0438\u0435 \u0444\u0443\u043D\u043A\u0446\u0438\u0438 \u0437\u0430\u0432\u0438\u0441\u0438\u0442 \u043E\u0442 \u0440\u0435\u0434\u0430\u043A\u0446\u0438\u0438), \u043A\u043E\u0442\u043E\u0440\u0430\u044F \u043F\u043E\u0437\u0432\u043E\u043B\u044F\u0435\u0442 \u041A\u043B\u0438\u0435\u043D\u0442\u0443 \u043D\u0430 \u043E\u0434\u043D\u043E\u0439 \u0445\u043E\u0441\u0442\u0438\u043D\u0433\u043E\u0432\u043E\u0439 \u043F\u043B\u043E\u0449\u0430\u0434\u043A\u0435 \u0441 \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u043D\u0438\u0435\u043C \u0435\u0434\u0438\u043D\u043E\u0433\u043E IP-\u0430\u0434\u0440\u0435\u0441\u0430 \u0441\u043E\u0437\u0434\u0430\u0432\u0430\u0442\u044C \u0438 \u0443\u0441\u0442\u0430\u043D\u0430\u0432\u043B\u0438\u0432\u0430\u0442\u044C \u043D\u0435\u043E\u0433\u0440\u0430\u043D\u0438\u0447\u0435\u043D\u043D\u043E\u0435 \u0447\u0438\u0441\u043B\u043E \u0441\u0430\u0439\u0442\u043E\u0432 \u0441 \u0440\u0430\u0437\u043D\u044B\u043C\u0438 \u0434\u043E\u043C\u0435\u043D\u043D\u044B\u043C\u0438 \u0438\u043C\u0435\u043D\u0430\u043C\u0438 \u0438\u0441\u043A\u043B\u044E\u0447\u0438\u0442\u0435\u043B\u044C\u043D\u043E \u0434\u043B\u044F \u0441\u043E\u0431\u0441\u0442\u0432\u0435\u043D\u043D\u043E\u0433\u043E \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u043D\u0438\u044F. \u0412 \u044D\u0442\u043E\u043C \u0441\u043B\u0443\u0447\u0430\u0435 \u0434\u043B\u044F \u0432\u0441\u0435\u0445 \u0441\u0430\u0439\u0442\u043E\u0432 \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u0443\u0435\u0442\u0441\u044F \u0435\u0434\u0438\u043D\u0430\u044F \u0431\u0430\u0437\u0430 \u0434\u0430\u043D\u043D\u044B\u0445. 3.2. \u041F\u0440\u0430\u0432\u0430 \u0438 \u043E\u0431\u044F\u0437\u0430\u043D\u043D\u043E\u0441\u0442\u0438 \u041A\u043B\u0438\u0435\u043D\u0442\u0430. 3.2.1. \u041A\u043B\u0438\u0435\u043D\u0442 \u043E\u0431\u043B\u0430\u0434\u0430\u0435\u0442 \u043F\u0440\u0430\u0432\u043E\u043C \u0441\u043E\u0437\u0434\u0430\u0432\u0430\u0442\u044C \u043D\u0430 \u043E\u0441\u043D\u043E\u0432\u0435 \u043F\u0440\u0438\u043E\u0431\u0440\u0435\u0442\u0430\u0435\u043C\u043E\u0439 \u043B\u0438\u0446\u0435\u043D\u0437\u0438\u0438 \u0438 \u0441 \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u043D\u0438\u0435\u043C \u0421\u0438\u0441\u0442\u0435\u043C\u044B \u0441\u0430\u0439\u0442\u044B \u0432 \u0441\u0435\u0442\u0438 \u0418\u043D\u0442\u0435\u0440\u043D\u0435\u0442 \u0432 \u043F\u0440\u0435\u0434\u0435\u043B\u0430\u0445, \u0443\u0441\u0442\u0430\u043D\u043E\u0432\u043B\u0435\u043D\u043D\u044B\u0445 \u043D\u0430\u0441\u0442\u043E\u044F\u0449\u0438\u043C \u0434\u043E\u0433\u043E\u0432\u043E\u0440\u043E\u043C \u0438 \u0432\u044B\u0431\u0440\u0430\u043D\u043D\u043E\u0439 \u041A\u043B\u0438\u0435\u043D\u0442\u043E\u043C \u0440\u0435\u0434\u0430\u043A\u0446\u0438\u0435\u0439 \u0421\u0438\u0441\u0442\u0435\u043C\u044B. 3.2.2. \u041A\u043B\u0438\u0435\u043D\u0442 \u043E\u0431\u043B\u0430\u0434\u0430\u0435\u0442 \u043F\u0440\u0430\u0432\u043E\u043C \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u0442\u044C \u043B\u043E\u0433\u043E\u0442\u0438\u043F\u044B \u0421\u0438\u0441\u0442\u0435\u043C\u044B \u043F\u0440\u0438 \u043F\u0440\u043E\u0432\u0435\u0434\u0435\u043D\u0438\u0438 \u0440\u0435\u043A\u043B\u0430\u043C\u043D\u044B\u0445 \u0430\u043A\u0446\u0438\u0439, \u0440\u0430\u0437\u043C\u0435\u0449\u0435\u043D\u0438\u0438 \u0438\u043D\u0444\u043E\u0440\u043C\u0430\u0446\u0438\u0438 \u043E \u0421\u0438\u0441\u0442\u0435\u043C\u0435 \u043D\u0430 \u0441\u0432\u043E\u0451\u043C \u0441\u0430\u0439\u0442\u0435, \u0441 \u0443\u043A\u0430\u0437\u0430\u043D\u0438\u0435\u043C \u043D\u0430 \u0438\u0441\u043A\u043B\u044E\u0447\u0438\u0442\u0435\u043B\u044C\u043D\u044B\u0435 \u043F\u0440\u0430\u0432\u0430 \u041F\u0440\u0430\u0432\u043E\u043E\u0431\u043B\u0430\u0434\u0430\u0442\u0435\u043B\u044F \u043D\u0430 \u0421\u0438\u0441\u0442\u0435\u043C\u0443. 3.2.3. \u041F\u0440\u043E\u0444\u0435\u0441\u0441\u0438\u043E\u043D\u0430\u043B\u044C\u043D\u044B\u0435 \u043A\u043E\u043D\u0441\u0443\u043B\u044C\u0442\u0430\u0446\u0438\u0438 \u043F\u043E \u0440\u0430\u0431\u043E\u0442\u0435 \u0421\u0438\u0441\u0442\u0435\u043C\u044B \u043F\u0440\u0435\u0434\u043E\u0441\u0442\u0430\u0432\u043B\u044F\u044E\u0442\u0441\u044F \u0421\u043B\u0443\u0436\u0431\u043E\u0439 \u0417\u0430\u0431\u043E\u0442\u044B \u042E\u043C\u0438\u0441\u043E\u0444\u0442 \u0431\u0435\u0437 \u0432\u0437\u0438\u043C\u0430\u043D\u0438\u044F \u0434\u043E\u043F\u043E\u043B\u043D\u0438\u0442\u0435\u043B\u044C\u043D\u043E\u0439 \u043E\u043F\u043B\u0430\u0442\u044B \u0432 \u0442\u0435\u0447\u0435\u043D\u0438\u0438 \u043E\u0434\u043D\u043E\u0433\u043E \u0433\u043E\u0434\u0430 \u0441 \u043C\u043E\u043C\u0435\u043D\u0442\u0430 \u043F\u0440\u0438\u043E\u0431\u0440\u0435\u0442\u0435\u043D\u0438\u044F \u043F\u0440\u0430\u0432\u0430 \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u043D\u0438\u044F \u0421\u0438\u0441\u0442\u0435\u043C\u044B,  \u0434\u0430\u043B\u0435\u0435 \u2013 \u043F\u0440\u0438 \u0443\u0441\u043B\u043E\u0432\u0438\u0438 \u043F\u0440\u0438\u043E\u0431\u0440\u0435\u0442\u0435\u043D\u0438\u044F \u041A\u043B\u0438\u0435\u043D\u0442\u043E\u043C \u0420\u041A\u041B \u043D\u0430 \u0441\u043E\u043E\u0442\u0432\u0435\u0442\u0441\u0442\u0432\u0443\u044E\u0449\u0438\u0439 \u0441\u0440\u043E\u043A. 3.2.4. \u041A\u043B\u0438\u0435\u043D\u0442 \u0432\u043F\u0440\u0430\u0432\u0435 \u0432\u043D\u043E\u0441\u0438\u0442\u044C \u0438\u0437\u043C\u0435\u043D\u0435\u043D\u0438\u044F \u0432 \u043F\u0440\u0430\u0432\u043E\u043C\u0435\u0440\u043D\u043E \u043F\u0440\u0438\u043E\u0431\u0440\u0435\u0442\u0451\u043D\u043D\u044B\u0439 \u044D\u043A\u0437\u0435\u043C\u043F\u043B\u044F\u0440 \u0421\u0438\u0441\u0442\u0435\u043C\u044B \u0438\u0441\u043A\u043B\u044E\u0447\u0438\u0442\u0435\u043B\u044C\u043D\u043E \u0432 \u0446\u0435\u043B\u044F\u0445 \u0444\u0443\u043D\u043A\u0446\u0438\u043E\u043D\u0438\u0440\u043E\u0432\u0430\u043D\u0438\u044F \u0421\u0438\u0441\u0442\u0435\u043C\u044B \u043D\u0430 \u043A\u043E\u043D\u043A\u0440\u0435\u0442\u043D\u044B\u0445 \u0442\u0435\u0445\u043D\u0438\u0447\u0435\u0441\u043A\u0438\u0445 \u0441\u0440\u0435\u0434\u0441\u0442\u0432\u0430\u0445 \u041A\u043B\u0438\u0435\u043D\u0442\u0430 \u0438\u043B\u0438 \u043F\u043E\u0434 \u0443\u043F\u0440\u0430\u0432\u043B\u0435\u043D\u0438\u0435\u043C \u043A\u043E\u043D\u043A\u0440\u0435\u0442\u043D\u044B\u0445 \u043F\u0440\u043E\u0433\u0440\u0430\u043C\u043C \u041A\u043B\u0438\u0435\u043D\u0442\u0430 (\u0430\u0434\u0430\u043F\u0442\u0430\u0446\u0438\u044F) \u0432 \u043F\u0440\u0435\u0434\u0435\u043B\u0430\u0445, \u0443\u043A\u0430\u0437\u0430\u043D\u043D\u044B\u0445 \u043D\u0430 \u0441\u0442\u0440\u0430\u043D\u0438\u0446\u0435 http://docs.umi-cms.ru/system_modification_rules/, \u0430 \u0442\u0430\u043A\u0436\u0435 \u0438\u0441\u043F\u0440\u0430\u0432\u043B\u044F\u0442\u044C \u044F\u0432\u043D\u044B\u0435 \u043E\u0448\u0438\u0431\u043A\u0438 \u0421\u0438\u0441\u0442\u0435\u043C\u044B. 4. \u0421\u041E\u0425\u0420\u0410\u041D\u0418\u0415 \u041F\u0420\u0410\u0412 \u0418 \u0421\u041E\u0411\u0421\u0422\u0412\u0415\u041D\u041D\u041E\u0421\u0422\u0418 4.1. \u041A\u043B\u0438\u0435\u043D\u0442 \u043C\u043E\u0436\u0435\u0442 \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u0442\u044C \u0421\u0438\u0441\u0442\u0435\u043C\u0443 \u0442\u043E\u043B\u044C\u043A\u043E \u0432 \u043F\u0440\u0435\u0434\u0435\u043B\u0430\u0445 \u0442\u0435\u0445 \u043F\u0440\u0430\u0432 \u0438 \u0442\u0435\u043C\u0438 \u0441\u043F\u043E\u0441\u043E\u0431\u0430\u043C\u0438, \u043A\u043E\u0442\u043E\u0440\u044B\u0435 \u043F\u0440\u0435\u0434\u0443\u0441\u043C\u043E\u0442\u0440\u0435\u043D\u044B \u043D\u0430\u0441\u0442\u043E\u044F\u0449\u0438\u043C \u0434\u043E\u0433\u043E\u0432\u043E\u0440\u043E\u043C. \u041F\u0440\u0430\u0432\u043E \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u043D\u0438\u044F \u0421\u0438\u0441\u0442\u0435\u043C\u044B, \u043F\u0440\u044F\u043C\u043E \u043D\u0435 \u0443\u043A\u0430\u0437\u0430\u043D\u043D\u043E\u0435 \u0432 \u043D\u0430\u0441\u0442\u043E\u044F\u0449\u0435\u043C \u0434\u043E\u0433\u043E\u0432\u043E\u0440\u0435, \u043D\u0435 \u0441\u0447\u0438\u0442\u0430\u0435\u0442\u0441\u044F \u043F\u0440\u0435\u0434\u043E\u0441\u0442\u0430\u0432\u043B\u0435\u043D\u043D\u044B\u043C \u041A\u043B\u0438\u0435\u043D\u0442\u0443. 4.2. \u0418\u0441\u043A\u043B\u044E\u0447\u0438\u0442\u0435\u043B\u044C\u043D\u044B\u0435 \u043F\u0440\u0430\u0432\u0430 \u043D\u0430 \u0421\u0438\u0441\u0442\u0435\u043C\u0443 \u043F\u0440\u0438\u043D\u0430\u0434\u043B\u0435\u0436\u0430\u0442 \u041F\u0440\u0430\u0432\u043E\u043E\u0431\u043B\u0430\u0434\u0430\u0442\u0435\u043B\u044E. 5. \u041E\u0413\u0420\u0410\u041D\u0418\u0427\u0415\u041D\u0418\u0415 \u041D\u0410 \u0412\u0421\u041A\u0420\u042B\u0422\u0418\u0415 \u0422\u0415\u0425\u041D\u041E\u041B\u041E\u0413\u0418\u0418 \u0418 \u0414\u0415\u041A\u041E\u041C\u041F\u0418\u041B\u042F\u0426\u0418\u042E 5.1.\u0417\u0430\u043F\u0440\u0435\u0449\u0430\u0435\u0442\u0441\u044F \u0432\u0441\u043A\u0440\u044B\u0432\u0430\u0442\u044C \u0442\u0435\u0445\u043D\u043E\u043B\u043E\u0433\u0438\u044E, \u0440\u0430\u0441\u0448\u0438\u0444\u0440\u043E\u0432\u044B\u0432\u0430\u0442\u044C, \u0434\u0435\u043A\u043E\u0434\u0438\u0440\u043E\u0432\u0430\u0442\u044C, \u043F\u0440\u043E\u0438\u0437\u0432\u043E\u0434\u0438\u0442\u044C \u043E\u0431\u0440\u0430\u0442\u043D\u044B\u0439 \u0438\u043D\u0436\u0438\u043D\u0438\u0440\u0438\u043D\u0433 \u0438\u043B\u0438 \u0434\u0435\u043A\u043E\u043C\u043F\u0438\u043B\u0438\u0440\u043E\u0432\u0430\u0442\u044C \u0441\u0438\u0441\u0442\u0435\u043C\u0443 \u0438 \u043B\u044E\u0431\u044B\u0435 \u0435\u0451 \u043A\u043E\u043C\u043F\u043E\u043D\u0435\u043D\u0442\u044B (\u0432\u043A\u043B\u044E\u0447\u0430\u044F \u0431\u0430\u0437\u0443 \u0434\u0430\u043D\u043D\u044B\u0445) \u0437\u0430 \u0438\u0441\u043A\u043B\u044E\u0447\u0435\u043D\u0438\u0435\u043C \u0441\u043B\u0443\u0447\u0430\u0435\u0432 \u0438 \u0442\u043E\u043B\u044C\u043A\u043E \u0432 \u0442\u043E\u0439 \u0441\u0442\u0435\u043F\u0435\u043D\u0438, \u043A\u043E\u0433\u0434\u0430 \u0442\u0430\u043A\u0438\u0435 \u0434\u0435\u0439\u0441\u0442\u0432\u0438\u044F \u044F\u0432\u043D\u043E \u0440\u0430\u0437\u0440\u0435\u0448\u0435\u043D\u044B \u0437\u0430\u043A\u043E\u043D\u043E\u0434\u0430\u0442\u0435\u043B\u044C\u0441\u0442\u0432\u043E\u043C, \u043D\u0435\u0441\u043C\u043E\u0442\u0440\u044F \u043D\u0430 \u043D\u0430\u043B\u0438\u0447\u0438\u0435 \u0432 \u043B\u0438\u0446\u0435\u043D\u0437\u0438\u043E\u043D\u043D\u043E\u043C \u0441\u043E\u0433\u043B\u0430\u0448\u0435\u043D\u0438\u0438 \u0434\u0430\u043D\u043D\u043E\u0433\u043E \u043E\u0433\u0440\u0430\u043D\u0438\u0447\u0435\u043D\u0438\u044F. 6. \u041F\u0415\u0420\u0415\u0414\u0410\u0427\u0410 \u041F\u0420\u041E\u0413\u0420\u0410\u041C\u041C\u041D\u041E\u0413\u041E \u041A\u041E\u0414\u0410 6.1. \u041F\u0435\u0440\u0435\u0434\u0430\u0447\u0430  \u041A\u043B\u0438\u0435\u043D\u0442\u0443 \u044D\u043A\u0437\u0435\u043C\u043F\u043B\u044F\u0440\u0430 \u0421\u0438\u0441\u0442\u0435\u043C\u044B \u043E\u0441\u0443\u0449\u0435\u0441\u0442\u0432\u043B\u044F\u0435\u0442\u0441\u044F \u043F\u0443\u0442\u0451\u043C \u0441\u043A\u0430\u0447\u0438\u0432\u0430\u043D\u0438\u044F \u0441 \u0441\u0430\u0439\u0442\u0430 \u041F\u0440\u0430\u0432\u043E\u043E\u0431\u043B\u0430\u0434\u0430\u0442\u0435\u043B\u044F \u043F\u043E\u0441\u0440\u0435\u0434\u0441\u0442\u0432\u043E\u043C \u0441\u0435\u0442\u0438 \u0418\u043D\u0442\u0435\u0440\u043D\u0435\u0442. 6.2. \u041B\u0438\u0446\u0435\u043D\u0437\u0438\u043E\u043D\u043D\u044B\u0439 \u043A\u043B\u044E\u0447 \u0434\u043B\u044F \u0430\u043A\u0442\u0438\u0432\u0430\u0446\u0438\u0438 \u0421\u0438\u0441\u0442\u0435\u043C\u044B \u043F\u0435\u0440\u0435\u0434\u0430\u0451\u0442\u0441\u044F \u041A\u043B\u0438\u0435\u043D\u0442\u0443 \u043F\u0443\u0442\u0435\u043C \u044D\u043B\u0435\u043A\u0442\u0440\u043E\u043D\u043D\u043E\u0439 \u043F\u043E\u0447\u0442\u044B (email) \u0432 \u0442\u0435\u0447\u0435\u043D\u0438\u0438 \u043F\u044F\u0442\u0438 \u0440\u0430\u0431\u043E\u0447\u0438\u0445 \u0434\u043D\u0435\u0439 \u0441 \u043C\u043E\u043C\u0435\u043D\u0442\u0430 \u0437\u0430\u0447\u0438\u0441\u043B\u0435\u043D\u0438\u044F \u0434\u0435\u043D\u0435\u0436\u043D\u044B\u0445 \u0441\u0440\u0435\u0434\u0441\u0442\u0432 \u043D\u0430 \u0440\u0430\u0441\u0447\u0451\u0442\u043D\u044B\u0439 \u0441\u0447\u0435\u0442 \u041F\u0440\u0430\u0432\u043E\u043E\u0431\u043B\u0430\u0434\u0430\u0442\u0435\u043B\u044F. 6.3. \u041F\u0440\u0430\u0432\u043E \u043D\u0430 \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u043D\u0438\u0435 \u0421\u0438\u0441\u0442\u0435\u043C\u044B \u0441\u0447\u0438\u0442\u0430\u0435\u0442\u0441\u044F \u043F\u0440\u0435\u0434\u043E\u0441\u0442\u0430\u0432\u043B\u0435\u043D\u043D\u044B\u043C \u041A\u043B\u0438\u0435\u043D\u0442\u0443 \u0441 \u043C\u043E\u043C\u0435\u043D\u0442\u0430 \u043F\u043E\u043B\u0443\u0447\u0435\u043D\u0438\u044F \u041A\u043B\u0438\u0435\u043D\u0442\u043E\u043C \u043B\u0438\u0446\u0435\u043D\u0437\u0438\u043E\u043D\u043D\u044B\u0445 \u043A\u043B\u044E\u0447\u0435\u0439 \u043E\u0442 \u041F\u0440\u0430\u0432\u043E\u043E\u0431\u043B\u0430\u0434\u0430\u0442\u0435\u043B\u044F. \u041F\u0440\u0435\u0434\u043E\u0441\u0442\u0430\u0432\u043B\u0435\u043D\u0438\u0435 \u0444\u0438\u043A\u0441\u0438\u0440\u0443\u0435\u0442\u0441\u044F \u0432 \u0434\u0432\u0443\u0441\u0442\u043E\u0440\u043E\u043D\u043D\u0435\u043C \u0410\u043A\u0442\u0435 \u043F\u0435\u0440\u0435\u0434\u0430\u0447\u0438 \u043F\u0440\u0430\u0432. 6.4. \u041F\u0440\u0430\u0432\u043E\u043E\u0431\u043B\u0430\u0434\u0430\u0442\u0435\u043B\u044C \u043E\u0444\u043E\u0440\u043C\u043B\u044F\u0435\u0442 \u0432 \u0434\u0432\u0443\u0445 \u044D\u043A\u0437\u0435\u043C\u043F\u043B\u044F\u0440\u0430\u0445 \u0410\u043A\u0442 \u043F\u0440\u0438\u0435\u043C\u0430-\u043F\u0435\u0440\u0435\u0434\u0430\u0447\u0438 \u0438 \u043D\u0430\u043F\u0440\u0430\u0432\u043B\u044F\u0435\u0442 \u0438\u0445 \u041A\u043B\u0438\u0435\u043D\u0442\u0443, \u043A\u043E\u0442\u043E\u0440\u044B\u0439 \u043E\u0431\u044F\u0437\u0430\u043D \u043F\u043E\u0434\u043F\u0438\u0441\u0430\u0442\u044C \u043F\u043E\u043B\u0443\u0447\u0435\u043D\u043D\u044B\u0435 \u044D\u043A\u0437\u0435\u043C\u043F\u043B\u044F\u0440\u044B \u0410\u043A\u0442\u0430 \u043F\u0440\u0438\u0451\u043C\u0430-\u043F\u0435\u0440\u0435\u0434\u0430\u0447\u0438 \u0438 \u0432\u0435\u0440\u043D\u0443\u0442\u044C \u043E\u0434\u0438\u043D \u044D\u043A\u0437\u0435\u043C\u043F\u043B\u044F\u0440 \u0410\u043A\u0442\u0430 \u041F\u0440\u0430\u0432\u043E\u043E\u0431\u043B\u0430\u0434\u0430\u0442\u0435\u043B\u044E \u0432 5-\u0442\u0438\u0434\u043D\u0435\u0432\u043D\u044B\u0439 \u0441\u0440\u043E\u043A \u0441 \u043C\u043E\u043C\u0435\u043D\u0442\u0430 \u043F\u043E\u043B\u0443\u0447\u0435\u043D\u0438\u044F, \u043B\u0438\u0431\u043E \u0432 \u0443\u043A\u0430\u0437\u0430\u043D\u043D\u044B\u0439 \u0441\u0440\u043E\u043A \u043F\u0440\u0435\u0434\u0441\u0442\u0430\u0432\u0438\u0442\u044C \u041F\u0440\u0430\u0432\u043E\u043E\u0431\u043B\u0430\u0434\u0430\u0442\u0435\u043B\u044E \u043C\u043E\u0442\u0438\u0432\u0438\u0440\u043E\u0432\u0430\u043D\u043D\u044B\u0435 \u0432\u043E\u0437\u0440\u0430\u0436\u0435\u043D\u0438\u044F \u043F\u0440\u043E\u0442\u0438\u0432 \u043F\u043E\u0434\u043F\u0438\u0441\u0430\u043D\u0438\u044F \u0410\u043A\u0442\u0430. \u0412 \u0441\u043B\u0443\u0447\u0430\u0435 \u043D\u0435\u043F\u043E\u043B\u0443\u0447\u0435\u043D\u0438\u044F \u041F\u0440\u0430\u0432\u043E\u043E\u0431\u043B\u0430\u0434\u0430\u0442\u0435\u043B\u0435\u043C \u0432 \u0443\u0441\u0442\u0430\u043D\u043E\u0432\u043B\u0435\u043D\u043D\u044B\u0439 \u043D\u0430\u0441\u0442\u043E\u044F\u0449\u0438\u043C \u043F\u0443\u043D\u043A\u0442\u043E\u043C \u0441\u0440\u043E\u043A \u043C\u043E\u0442\u0438\u0432\u0438\u0440\u043E\u0432\u0430\u043D\u043D\u044B\u0445 \u0432\u043E\u0437\u0440\u0430\u0436\u0435\u043D\u0438\u0439 \u043E\u0442 \u041A\u043B\u0438\u0435\u043D\u0442\u0430, \u043F\u0440\u0430\u0432\u043E \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u043D\u0438\u044F \u044D\u043A\u0437\u0435\u043C\u043F\u043B\u044F\u0440\u043E\u0432 \u0421\u0438\u0441\u0442\u0435\u043C\u044B, \u0443\u043A\u0430\u0437\u0430\u043D\u043D\u044B\u0445 \u0432 \u0442\u0430\u043A\u043E\u043C \u0410\u043A\u0442\u0435, \u0441\u0447\u0438\u0442\u0430\u044E\u0442\u0441\u044F \u043F\u0440\u0435\u0434\u043E\u0441\u0442\u0430\u0432\u043B\u0435\u043D\u043D\u044B\u043C\u0438 (\u043F\u0435\u0440\u0435\u0434\u0430\u043D\u043D\u044B\u043C\u0438) \u041A\u043B\u0438\u0435\u043D\u0442\u0443 \u043D\u0430\u0434\u043B\u0435\u0436\u0430\u0449\u0438\u043C \u043E\u0431\u0440\u0430\u0437\u043E\u043C \u0438 \u043F\u0440\u0438\u043D\u044F\u0442\u044B\u043C\u0438 \u0438\u043C \u0432 \u043F\u043E\u043B\u043D\u043E\u043C \u043E\u0431\u044A\u0451\u043C\u0435 \u0431\u0435\u0437 \u043A\u0430\u043A\u0438\u0445-\u043B\u0438\u0431\u043E \u0432\u043E\u0437\u0440\u0430\u0436\u0435\u043D\u0438\u0439. 6.5. \u041F\u0440\u043E\u0432\u0435\u0440\u043A\u0430 \u043D\u0430\u0438\u043C\u0435\u043D\u043E\u0432\u0430\u043D\u0438\u044F, \u043A\u043E\u043D\u0444\u0438\u0433\u0443\u0440\u0430\u0446\u0438\u0438, \u0438\u043D\u044B\u0445 \u0434\u0430\u043D\u043D\u044B\u0445, \u043A\u0430\u0441\u0430\u044E\u0449\u0438\u0445\u0441\u044F \u043F\u0440\u0435\u0434\u043E\u0441\u0442\u0430\u0432\u043B\u044F\u0435\u043C\u044B\u0445 \u043F\u0440\u0430\u0432 \u043D\u0430 \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u043D\u0438\u0435 \u044D\u043A\u0437\u0435\u043C\u043F\u043B\u044F\u0440\u043E\u0432 \u0421\u0438\u0441\u0442\u0435\u043C\u044B, \u043E\u0441\u0443\u0449\u0435\u0441\u0442\u0432\u043B\u044F\u0435\u0442\u0441\u044F \u041A\u043B\u0438\u0435\u043D\u0442\u043E\u043C \u0432 \u043F\u0435\u0440\u0438\u043E\u0434, \u043F\u0440\u0435\u0434\u043E\u0441\u0442\u0430\u0432\u043B\u0435\u043D\u043D\u044B\u0445 \u0434\u043B\u044F \u043F\u043E\u0434\u043F\u0438\u0441\u0430\u043D\u0438\u044F \u0410\u043A\u0442\u0430 \u043F\u0440\u0438\u0451\u043C\u0430-\u043F\u0435\u0440\u0435\u0434\u0430\u0447\u0438. \u0412 \u0441\u043B\u0443\u0447\u0430\u0435 \u0432\u044B\u044F\u0432\u043B\u0435\u043D\u0438\u044F \u043A\u0430\u043A\u0438\u0445-\u043B\u0438\u0431\u043E \u043D\u0435\u0441\u043E\u043E\u0442\u0432\u0435\u0442\u0441\u0442\u0432\u0438\u0439 \u041A\u043B\u0438\u0435\u043D\u0442 \u043D\u0430\u043F\u0440\u0430\u0432\u043B\u044F\u0435\u0442 \u041F\u0440\u0430\u0432\u043E\u043E\u0431\u043B\u0430\u0434\u0430\u0442\u0435\u043B\u044E \u0441\u043E\u043E\u0442\u0432\u0435\u0442\u0441\u0442\u0432\u0443\u044E\u0449\u0438\u0435 \u0432\u043E\u0437\u0440\u0430\u0436\u0435\u043D\u0438\u044F. \u0412 \u044D\u0442\u043E\u043C \u0441\u043B\u0443\u0447\u0430\u0435, \u043C\u043E\u043C\u0435\u043D\u0442\u043E\u043C \u043F\u0435\u0440\u0435\u0434\u0430\u0447\u0438 \u043F\u0440\u0430\u0432 \u0441\u0447\u0438\u0442\u0430\u0435\u0442\u0441\u044F \u0434\u0430\u0442\u0430 \u0443\u0440\u0435\u0433\u0443\u043B\u0438\u0440\u043E\u0432\u0430\u043D\u0438\u044F \u041F\u0440\u0430\u0432\u043E\u043E\u0431\u043B\u0430\u0434\u0430\u0442\u0435\u043B\u0435\u043C \u043E\u0431\u043E\u0441\u043D\u043E\u0432\u0430\u043D\u043D\u044B\u0445 \u0432\u043E\u0437\u0440\u0430\u0436\u0435\u043D\u0438\u0439 \u041A\u043B\u0438\u0435\u043D\u0442\u0430. 6.6. \u041F\u0440\u0430\u0432\u0430 \u043D\u0430 \u0438\u0441\u043F\u043E\u043B\u044C\u0437\u043E\u0432\u0430\u043D\u0438\u0435  [\u043A\u043E\u043D\u043A\u0440\u0435\u0442\u043D\u043E\u0439 \u043F\u0435\u0440\u0435\u0434\u0430\u043D\u043D\u043E\u0439 \u0440\u0435\u0434\u0430\u043A\u0446\u0438\u0438 \u0438 \u0432\u0435\u0440\u0441\u0438\u0438] \u0421\u0438\u0441\u0442\u0435\u043C\u044B \u043F\u0440\u0435\u0434\u043E\u0441\u0442\u0430\u0432\u043B\u044F\u044E\u0442\u0441\u044F \u043D\u0430 \u0432\u0435\u0441\u044C \u0441\u0440\u043E\u043A \u0434\u0435\u0439\u0441\u0442\u0432\u0438\u044F \u0438\u0441\u043A\u043B\u044E\u0447\u0438\u0442\u0435\u043B\u044C\u043D\u043E\u0433\u043E \u043F\u0440\u0430\u0432\u0430.  \u041F\u0440\u0435\u0434\u043E\u0441\u0442\u0430\u0432\u043B\u0435\u043D\u0438\u0435  \u0438\u043D\u044B\u0445 \u0440\u0435\u0434\u0430\u043A\u0446\u0438\u0439 \u0438/\u0438\u043B\u0438 \u0432\u0435\u0440\u0441\u0438\u0439 \u0421\u0438\u0441\u0442\u0435\u043C\u044B \u043C\u043E\u0436\u0435\u0442 \u044F\u0432\u043B\u044F\u0442\u044C\u0441\u044F \u043F\u0440\u0435\u0434\u043C\u0435\u0442\u043E\u043C \u0438\u043D\u044B\u0445 [\u043E\u0441\u043D\u043E\u0432\u0430\u043D\u043D\u044B\u0445 \u043D\u0430 \u0437\u0430\u043A\u0430\u0437\u0430\u0445 \u041A\u043B\u0438\u0435\u043D\u0442\u0430] \u0441\u0434\u0435\u043B\u043E\u043A \u0441\u0442\u043E\u0440\u043E\u043D. 6.7. \u041A\u043B\u0438\u0435\u043D\u0442\u0443 \u0440\u0430\u0437\u0440\u0435\u0448\u0430\u0435\u0442\u0441\u044F