import { INCREMENT, DECREMENT, RESET } from './actions';
import {useSelector} from "react-redux";
import * as counterSelectors from "./selector";

const initialState = {
    count: 0
};

function User() { // Rule 2: call hooks in function component
    const count = useSelector(counterSelectors.count); // Rule 1: call hooks in top-level
    return count
}

export default function reducer(state = initialState, action) {
    console.log('reducer', state, action);
    switch(action.type) {
        case INCREMENT:
            return {
                count: state.count + 1
            };
        case DECREMENT:
            return {
                count: state.count - 1
            };
        case RESET:
            return {
                count: 0
            };
        default:
            return state;
    }
}
